// frontend/src/App.js
import React, { useState } from "react";

function App() {
  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    data_nascimento: "",
    objetivo: "",
    cursos: "",
    experiencias: "",
    cnh: "",
    idioma: ""
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5000/generate_pdf", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form)
      });
      if (!res.ok) throw new Error("Erro ao gerar PDF");
      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      link.href = url;
      const safeName = (form.nome || "curriculo").replace(/\s+/g, "_");
      link.download = `curriculo_${safeName}.pdf`;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert(err.message || "Erro desconhecido");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: "2rem auto", padding: "1rem", fontFamily: "Arial, sans-serif" }}>
      <h1>Gerador de Currículo (PDF)</h1>
      <form onSubmit={handleSubmit}>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "0.5rem" }}>
          <label>
            Nome
            <input name="nome" value={form.nome} onChange={handleChange} required />
          </label>
          <label>
            Telefone
            <input name="telefone" value={form.telefone} onChange={handleChange} />
          </label>
          <label>
            Email
            <input name="email" value={form.email} onChange={handleChange} type="email" />
          </label>
          <label>
            Data de nascimento
            <input name="data_nascimento" value={form.data_nascimento} onChange={handleChange} type="date" />
          </label>
        </div>

        <label style={{ display: "block", marginTop: "0.8rem" }}>
          Objetivo
          <textarea name="objetivo" value={form.objetivo} onChange={handleChange} rows={3} />
        </label>

        <label style={{ display: "block", marginTop: "0.8rem" }}>
          Cursos (separe por linhas)
          <textarea name="cursos" value={form.cursos} onChange={handleChange} rows={3} />
        </label>

        <label style={{ display: "block", marginTop: "0.8rem" }}>
          Experiências (separe por linhas)
          <textarea name="experiencias" value={form.experiencias} onChange={handleChange} rows={4} />
        </label>

        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "0.5rem", marginTop: "0.6rem" }}>
          <label>
            CNH
            <input name="cnh" value={form.cnh} onChange={handleChange} />
          </label>
          <label>
            Idiomas
            <input name="idioma" value={form.idioma} onChange={handleChange} />
          </label>
        </div>

        <div style={{ marginTop: "1rem" }}>
          <button type="submit" disabled={loading}>
            {loading ? "Gerando..." : "Gerar PDF"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default App;
