import React, { useState, useEffect, useRef } from "react";
import "./login.css";

function Login() {
  const [open, setOpen] = useState(false); // controla se está aberto
  const [fixed, setFixed] = useState(false); // controla se está "fixado" pelo clique
  const containerRef = useRef(null);

  // Fecha ao clicar fora
  useEffect(() => {
    function handleClickOutside(e) {
      if (containerRef.current && !containerRef.current.contains(e.target)) {
        setOpen(false);
        setFixed(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div
      ref={containerRef}
      className="login-container"
      onMouseEnter={() => !fixed && setOpen(true)}
      onMouseLeave={() => !fixed && setOpen(false)}
    >
      <a
        href="#login"
        className="login-link"
        onClick={(e) => {
          e.preventDefault();
          setFixed(!fixed);
          setOpen(!fixed ? true : false);
        }}
      >
        <i className="bi bi-person-circle"></i> Login
      </a>

      <div className={`login-submenu ${open ? "open" : ""}`}>
        <form className="login-form">
          <input type="text" placeholder="Usuário" className="login-input" />
          <input type="password" placeholder="Senha" className="login-input" />
          <button type="submit" className="btn-login">
            Entrar
          </button>
        </form>
        <p className="cadastre-text">
          Não tem uma conta?{" "}
          <a href="/cadastro" className="cadastre-link">
            Cadastre-se
          </a>
        </p>
      </div>
    </div>
  );
}

export default Login;
