import React, { useState } from "react";
import "./App.css";

function App() {
  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    data_nascimento: "",
    cidade: "",
    objetivo: "",
    formacoes: [],
    conhecimentos: [],
    cursos: [],
    experiencias: [],
    idiomas: [],
    cnh: "",
  });

  const [loading, setLoading] = useState(false);

  const capitalize = (text) =>
    text.replace(/\b\w/g, (char) => char.toUpperCase());

  const handleChange = (e) => {
    const { name, value } = e.target;
    let val = value;

    if (name === "telefone") {
      val = val.replace(/\D/g, "");
      if (val.length > 13) return;
    }
    if (name === "email") val = val.toLowerCase();
    if (name === "cnh") {
      val = val.replace(/[^A-Za-z]/g, "").toUpperCase();
      if (val.length > 5) return;
    }
    if (["nome", "cidade", "objetivo"].includes(name)) val = capitalize(val);

    setForm((prev) => ({ ...prev, [name]: val }));
  };

  // Campos dinâmicos
  const handleArrayChange = (index, field, subField, value) => {
    const updated = [...form[field]];
    updated[index][subField] = value;
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  const addItem = (field, itemTemplate) => {
    setForm((prev) => ({ ...prev, [field]: [...prev[field], itemTemplate] }));
  };

  const removeItem = (field, index) => {
    const updated = [...form[field]];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.nome || !form.email || !form.telefone || !form.objetivo) {
      alert("Preencha os campos obrigatórios!");
      return;
    }
    if (!form.email.includes("@")) {
      alert("E-mail inválido!");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/generate_pdf`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (!res.ok) throw new Error("Erro ao gerar PDF");

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      const safeName = (form.nome || "curriculo").replace(/\s+/g, "_");
      link.href = url;
      link.download = `curriculo_${safeName}.pdf`;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert(err.message || "Erro desconhecido");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <section className="form-section">
        <h1>GERADOR DE CURRÍCULO ONLINE</h1>
        <form onSubmit={handleSubmit}>
          {/* Campos principais */}
          <div className="input-grid">
            <label>
              Nome *
              <input name="nome" value={form.nome} onChange={handleChange} required />
            </label>
            <label>
              Telefone *
              <input
                name="telefone"
                value={form.telefone}
                onChange={handleChange}
                placeholder="DDD9XXXXXXXX"
                required
              />
            </label>
            <label>
              Email *
              <input
                name="email"
                value={form.email}
                onChange={handleChange}
                type="email"
                required
              />
            </label>
            <label>
              Cidade
              <input name="cidade" value={form.cidade} onChange={handleChange} />
            </label>
            <label>
              Data de nascimento
              <input
                name="data_nascimento"
                value={form.data_nascimento}
                onChange={handleChange}
                type="date"
              />
            </label>
            <label>
              CNH
              <input name="cnh" value={form.cnh} onChange={handleChange} />
            </label>
          </div>

          <label>
            Objetivo Profissional *
            <textarea
              name="objetivo"
              value={form.objetivo}
              onChange={handleChange}
              rows={3}
              required
            />
          </label>

          {/* Formação Acadêmica */}
          <div className="dynamic-field">
            <label>Formação Acadêmica</label>
            {form.formacoes.map((item, i) => (
              <div key={i} className="multi-input">
                <input
                  placeholder="Curso"
                  value={item.curso}
                  onChange={(e) =>
                    handleArrayChange(i, "formacoes", "curso", capitalize(e.target.value))
                  }
                />
                <input
                  placeholder="Escola"
                  value={item.escola}
                  onChange={(e) =>
                    handleArrayChange(i, "formacoes", "escola", capitalize(e.target.value))
                  }
                />
                <select
                  value={item.status}
                  onChange={(e) =>
                    handleArrayChange(i, "formacoes", "status", e.target.value)
                  }
                >
                  <option value="">Status</option>
                  <option>Concluído</option>
                  <option>Cursando</option>
                  <option>Trancado</option>
                </select>
                <input
                  type="month"
                  placeholder="Início"
                  value={item.inicio}
                  onChange={(e) => handleArrayChange(i, "formacoes", "inicio", e.target.value)}
                />
                <input
                  type="month"
                  placeholder="Término"
                  value={item.fim}
                  onChange={(e) => handleArrayChange(i, "formacoes", "fim", e.target.value)}
                />
                <button
                  type="button"
                  className="remove-btn"
                  onClick={() => removeItem("formacoes", i)}
                >
                  Remover
                </button>
              </div>
            ))}
            <button
              type="button"
              className="add-btn"
              onClick={() =>
                addItem("formacoes", {
                  curso: "",
                  escola: "",
                  status: "",
                  inicio: "",
                  fim: "",
                })
              }
            >
              + Adicionar formação
            </button>
          </div>

          {/* Repetir o mesmo padrão com botão “Remover” para Conhecimentos, Cursos, Experiências e Idiomas */}
          {/* ... (pode copiar da sua versão anterior, só incluir o botão remove-btn igual ao de cima) */}

          <button type="submit" disabled={loading} className="submit-btn">
            {loading ? "Gerando..." : "Gerar PDF"}
          </button>
        </form>
      </section>
    </div>
  );
}

export default App;
