import React, { useState, useEffect, useRef } from "react";
import "./login.css";

function Login() {
  const [open, setOpen] = useState(false);
  const [fixed, setFixed] = useState(false);
  const containerRef = useRef(null);

  // Fecha se clicar fora
  useEffect(() => {
    function handleClickOutside(e) {
      if (containerRef.current && !containerRef.current.contains(e.target)) {
        setOpen(false);
        setFixed(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div
      ref={containerRef}
      className="login-container"
      onMouseEnter={() => {
        if (!fixed) setOpen(true);
      }}
      onMouseLeave={(e) => {
        // Só fecha se não estiver fixo e o mouse realmente sair da área toda (container + submenu)
        if (!fixed && !e.currentTarget.contains(e.relatedTarget)) {
          setOpen(false);
        }
      }}
    >
      <a
        href="#login"
        className={`login-link ${open || fixed ? "open" : ""}`}
        onClick={(e) => {
          e.preventDefault();
          if (fixed) {
            // se já estava fixo, clicar fecha
            setFixed(false);
            setOpen(false);
          } else {
            // fixa o submenu
            setFixed(true);
            setOpen(true);
          }
        }}
      >
        <i className="bi bi-person-circle"></i> Login
      </a>

      <div className={`login-submenu ${open ? "open" : ""}`}>
        <form className="login-form">
          <input type="text" placeholder="Usuário" className="login-input" />
          <input type="password" placeholder="Senha" className="login-input" />
          <button type="submit" className="btn-login">
            Entrar
          </button>
        </form>
        <p className="cadastre-text">
          Não tem uma conta?{" "}
          <a href="/cadastro" className="cadastre-link">
            Cadastre-se
          </a>
        </p>
      </div>
    </div>
  );
}

export default Login;
