import React, { useState, useRef } from "react";
import "./gerador.css";
import "../../App.css";
import Login from "../../components/login/login";

function Gerador() {
  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    data_nascimento: "",
    cidade: "",
    objetivo: "",
    formacoes: [],
    conhecimentos: [],
    cursos: [],
    experiencias: [],
    idiomas: [],
    cnh: "", // será preenchido se o usuário adicionar CNH
  });
  const [loading, setLoading] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  // preview visível?
  const [previewOpen, setPreviewOpen] = useState(false);
  // CNH field visible?
  const [showCNHField, setShowCNHField] = useState(false);

  const previewRef = useRef(null);

  const toggleMenu = () => setMenuOpen(!menuOpen);
  const togglePreview = () => setPreviewOpen((s) => !s);

  // saferCapitalize
  const saferCapitalize = (text) => {
    if (!text) return "";
    return text
      .split(" ")
      .map((w) => {
        if (!w) return "";
        const first = w[0].toUpperCase();
        const rest = w.slice(1).toLowerCase();
        return first + rest;
      })
      .join(" ");
  };

  const formatPhone = (onlyDigits) => {
    const d = onlyDigits.replace(/\D/g, "");
    let out = d;
    if (d.length <= 2) {
      out = "(" + d;
    } else if (d.length <= 6) {
      out = `(${d.slice(0, 2)}) ${d.slice(2)}`;
    } else if (d.length <= 10) {
      out = `(${d.slice(0, 2)}) ${d.slice(2, d.length - 4)}-${d.slice(d.length - 4)}`;
    } else {
      out = `(${d.slice(0, 2)}) ${d.slice(2, 7)}-${d.slice(7, 11)}`;
    }
    return out;
  };

  // onChange global
  const handleChange = (e) => {
    const { name, value } = e.target;
    let val = value;

    if (name === "telefone") {
      const digits = val.replace(/\D/g, "").slice(0, 11);
      val = formatPhone(digits);
    }

    if (name === "email") {
      val = val.toLowerCase();
    }

    if (name === "cnh") {
      val = val.replace(/[^0-9A-Za-z]/g, "").toUpperCase();
    }

    if (name === "nome" || name === "cidade") {
      val = saferCapitalize(val);
    }

    setForm((prev) => ({ ...prev, [name]: val }));
  };

  // arrays dinamicos
  const handleArrayChange = (index, field, subField, value) => {
    const updated = [...form[field]];
    updated[index] = { ...updated[index], [subField]: value };
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  const addItem = (field, itemTemplate) => {
    setForm((prev) => ({ ...prev, [field]: [...prev[field], itemTemplate] }));
  };

  const removeItem = (field, index) => {
    const updated = [...form[field]];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  // submit -> gerar PDF
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.nome || !form.email || !form.telefone || !form.objetivo) {
      alert("Preencha os campos obrigatórios!");
      return;
    }

    setLoading(true);
    try {
      await gerarPDF();
    } catch (err) {
      console.error(err);
      alert("Erro ao gerar PDF");
    } finally {
      setLoading(false);
    }
  };

  // gerar PDF usando html2pdf.js
  const gerarPDF = async () => {
    // garantir que o elemento do preview exista e esteja visível durante a geração
    const elemento = previewRef.current;
    if (!elemento) {
      alert("Preview não encontrado.");
      return;
    }

    const wasPreviewVisible = previewOpen;
    if (!wasPreviewVisible) setPreviewOpen(true); // mostrar temporariamente

    // esperar um frame para renderizar
    await new Promise((r) => setTimeout(r, 100));

    const opcoes = {
      margin: 10,
      filename: `curriculo_${(form.nome || "usuario").replace(/\s+/g, "_")}.pdf`,
      image: { type: "jpeg", quality: 0.98 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "mm", format: "a4", orientation: "portrait" },
    };

    // html2pdf deve estar disponível (ver instrução de CDN)
    if (!window.html2pdf) {
      alert("Biblioteca html2pdf.js não encontrada. Adicione o script no index.html conforme instruções.");
      if (!wasPreviewVisible) setPreviewOpen(false);
      return;
    }

    try {
      await window.html2pdf().set(opcoes).from(elemento).save();
    } catch (err) {
      console.error("Erro html2pdf:", err);
      alert("Erro ao gerar PDF.");
    } finally {
      if (!wasPreviewVisible) setPreviewOpen(false); // restaurar
    }
  };

  // render preview sections helpers
  const calcAge = (dateString) => {
    if (!dateString) return "";
    const dob = new Date(dateString);
    const diff = Date.now() - dob.getTime();
    const age = new Date(diff).getUTCFullYear() - 1970;
    return age;
  };

  return (
    <div className="app-container gerador-container">
      <nav className="top-navbar">
        <div className="left-section">
          <a href="#top">
            <img src="/logo.png" alt="Logo" className="logo" />
          </a>
        </div>
        <div className={`right-section ${menuOpen ? "open" : ""}`}>
          <a href="/" onClick={() => setMenuOpen(false)}>
            Voltar para a página inicial
          </a>
          <Login />
        </div>

        <div className="menu-toggle" onClick={toggleMenu}>
          <i className={`bi ${menuOpen ? "bi-x" : "bi-list"}`}></i>
        </div>
      </nav>

      <section className="form-section" id="form-section">
        <h1>GERADOR DE CURRÍCULO ONLINE</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-grid">
            <label className="field-label">
              Nome
              <input name="nome" value={form.nome} onChange={handleChange} required placeholder="Digite seu nome completo" />
            </label>

            <label className="field-label">
              Telefone
              <input name="telefone" value={form.telefone} onChange={handleChange} placeholder="Apenas números" required />
            </label>

            <label className="field-label">
              Email
              <input name="email" value={form.email} onChange={handleChange} type="email" required placeholder="nome@exemplo.com" />
            </label>

            <label className="field-label">
              Cidade
              <input name="cidade" value={form.cidade} onChange={handleChange} placeholder="Informe sua cidade" />
            </label>

            <label className="field-label">
              Data de nascimento
              <input name="data_nascimento" value={form.data_nascimento} onChange={handleChange} type="date" />
            </label>

            {/* removi o campo CNH daqui (foi movido para área dinâmica abaixo) */}
          </div>

          <label className="field-label">
            Objetivo Profissional
            <textarea name="objetivo" value={form.objetivo} onChange={handleChange} rows={4} required placeholder="Digite aqui o seus objetivos e metas" />
          </label>

          {/* Formação Acadêmica */}
          <div className="dynamic-field">
            <div className="section-title">Formação Acadêmica</div>
            {form.formacoes.map((item, i) => {
              const status = item.status || "";
              return (
                <div key={i} className={`multi-input ${Array.isArray(item.atribuicoes) ? "" : "stack"}`}>
                  <label className="field-label"> Nome da Formação
                    <input placeholder="Engenharia, Medicina..." value={item.curso} onChange={(e) => handleArrayChange(i, "formacoes", "curso", saferCapitalize(e.target.value))} />
                  </label>
                  <label className="field-label"> Nome da Instituição
                    <input placeholder="Informe o nome da instituição" value={item.escola} onChange={(e) => handleArrayChange(i, "formacoes", "escola", saferCapitalize(e.target.value))} />
                  </label>
                  <label className="field-label"> Status da Formação
                    <select value={status} onChange={(e) => handleArrayChange(i, "formacoes", "status", e.target.value)}>
                      <option value="">Status</option>
                      <option value="Concluído">Concluído</option>
                      <option value="Cursando">Cursando</option>
                      <option value="Trancado">Trancado</option>
                    </select>
                  </label>
                  <label className="field-label"> Data de Início
                    <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "formacoes", "inicio", e.target.value)} />
                  </label>

                  <div className={`hide-animate ${status === "Cursando" ? "hidden" : ""}`}>
                    <label className="field-label"> Data de Término
                      <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "formacoes", "fim", e.target.value)} />
                    </label>
                  </div>

                  <button type="button" className="remove-btn" onClick={() => removeItem("formacoes", i)}>Remover Formação</button>
                  <hr className="divider" />
                </div>
              );
            })}
            <button type="button" className="add-btn" onClick={() => addItem("formacoes", { curso: "", escola: "", status: "", inicio: "", fim: "" })}>+ Adicionar formação</button>
          </div>

          {/* Conhecimentos */}
          <div className="dynamic-field">
            <div className="section-title">Conhecimentos</div>
            {form.conhecimentos.map((item, i) => (
              <div key={i} className="multi-input stack">
                <label className="field-label"> Descrição do Conhecimento</label>
                <input placeholder="Informe um conhecimento (ex: Pacote Office, Python, etc.)" value={item.descricao} onChange={(e) => handleArrayChange(i, "conhecimentos", "descricao", e.target.value)} />
                <button type="button" className="remove-btn" onClick={() => removeItem("conhecimentos", i)}>Remover conhecimento</button>
                <hr className="divider" />
              </div>
            ))}
            <button type="button" className="add-btn" onClick={() => addItem("conhecimentos", { descricao: "" })}>+ Adicionar conhecimento</button>
          </div>

          {/* Cursos */}
          <div className="dynamic-field">
            <div className="section-title">Cursos de Qualificação</div>
            {form.cursos.map((item, i) => {
              const status = item.status || "";
              return (
                <div key={i} className="multi-input">
                  <label className="field-label"> Nome do Curso
                    <input placeholder="Informe o nome do curso" value={item.curso} onChange={(e) => handleArrayChange(i, "cursos", "curso", saferCapitalize(e.target.value))} />
                  </label>
                  <label className="field-label"> Nome da Instituição
                    <input placeholder="Informe o nome da instituição" value={item.instituicao} onChange={(e) => handleArrayChange(i, "cursos", "instituicao", saferCapitalize(e.target.value))} />
                  </label>
                  <label className="field-label"> Status do Curso
                    <select value={status} onChange={(e) => handleArrayChange(i, "cursos", "status", e.target.value)}>
                      <option value="">Status</option>
                      <option value="Concluído">Concluído</option>
                      <option value="Cursando">Cursando</option>
                    </select>
                  </label>

                  <label className="field-label"> Data de Início
                    <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "cursos", "inicio", e.target.value)} />
                  </label>

                  <div className={`hide-animate ${status === "Cursando" ? "hidden" : ""}`}>
                    <label className="field-label"> Data de Término
                      <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "cursos", "fim", e.target.value)} />
                    </label>
                  </div>

                  <button type="button" className="remove-btn" onClick={() => removeItem("cursos", i)}>Remover curso</button>
                  <hr className="divider" />
                </div>
              );
            })}
            <button type="button" className="add-btn" onClick={() => addItem("cursos", { curso: "", instituicao: "", inicio: "", fim: "", status: "" })}>+ Adicionar curso</button>
          </div>

          {/* Experiências */}
          <div className="dynamic-field">
            <div className="section-title">Experiência Profissional</div>
            {form.experiencias.map((item, i) => {
              const atual = !!item.trabalhoAtual;
              return (
                <div key={i} className="multi-input">
                  <label className="field-label"> Nome da Empresa
                    <input placeholder="Informe o nome da empresa" value={item.empresa} onChange={(e) => handleArrayChange(i, "experiencias", "empresa", saferCapitalize(e.target.value))} />
                  </label>
                  <label className="field-label"> Cargo
                    <input placeholder="Informe o nome do cargo" value={item.cargo} onChange={(e) => handleArrayChange(i, "experiencias", "cargo", saferCapitalize(e.target.value))} />
                  </label>

                  <button type="button" className="remove-btn" onClick={() => removeItem("experiencias", i)}>Remover experiência</button>

                  <label className="field-label"> Data de Início
                    <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "experiencias", "inicio", e.target.value)} />
                  </label>

                  <div className={`hide-animate ${atual ? "hidden" : ""}`}>
                    <label className="field-label"> Data de Término
                      <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "experiencias", "fim", e.target.value)} />
                    </label>
                  </div>

                  <label style={{ display: "flex", alignItems: "center", gap: "10px", marginLeft: "2vw", marginTop: "2.7rem", justifyContent: "center" }}>
                    <input type="checkbox" checked={atual} onChange={(e) => handleArrayChange(i, "experiencias", "trabalhoAtual", e.target.checked)} />
                    Trabalho atual
                  </label>

                  <div className="multi-input stack" style={{ marginTop: "0.5rem" }}>
                    <label className="field-label">Atribuições</label>

                    {(item.atribuicoes || []).map((atr, j) => (
                      <div key={j} className="atribuicao-item">
                        <input placeholder={`Atribuição ${j + 1}`} value={atr} onChange={(e) => {
                          const exp = [...form.experiencias];
                          exp[i].atribuicoes[j] = e.target.value;
                          setForm((prev) => ({ ...prev, experiencias: exp }));
                        }} />
                        <button type="button" className="remove-btn" onClick={() => {
                          const exp = [...form.experiencias];
                          exp[i].atribuicoes.splice(j, 1);
                          setForm((prev) => ({ ...prev, experiencias: exp }));
                        }}>✕</button>
                      </div>
                    ))}

                    <button type="button" className="add-btn" onClick={() => {
                      const exp = [...form.experiencias];
                      exp[i].atribuicoes = [...(exp[i].atribuicoes || []), ""];
                      setForm((prev) => ({ ...prev, experiencias: exp }));
                    }}>+ Adicionar atribuição</button>

                  </div>

                  <hr className="divider" />
                </div>
              );
            })}
            <button type="button" className="add-btn" onClick={() => addItem("experiencias", { empresa: "", cargo: "", trabalhoAtual: false, inicio: "", fim: "", atribuicoes: [] })}>+ Adicionar experiência</button>
          </div>

          {/* Idiomas */}
          <div className="dynamic-field">
            <div className="section-title">Idiomas</div>
            {form.idiomas.map((item, i) => (
              <div key={i} className="multi-input">
                <label className="field-label"> Idioma
                  <select value={item.idioma} onChange={(e) => handleArrayChange(i, "idiomas", "idioma", e.target.value)}>
                    <option value="">Selecione o idioma</option>
                    <option value="Alemão">Alemão</option>
                    <option value="Árabe">Árabe</option>
                    <option value="Chinês (Mandarim)">Chinês (Mandarim)</option>
                    <option value="Coreano">Coreano</option>
                    <option value="Dinamarquês">Dinamarquês</option>
                    <option value="Espanhol">Espanhol</option>
                    <option value="Finlandês">Finlandês</option>
                    <option value="Francês">Francês</option>
                    <option value="Grego">Grego</option>
                    <option value="Hebraico">Hebraico</option>
                    <option value="Hindi">Hindi</option>
                    <option value="Holandês">Holandês</option>
                    <option value="Húngaro">Húngaro</option>
                    <option value="Inglês">Inglês</option>
                    <option value="Italiano">Italiano</option>
                    <option value="Japonês">Japonês</option>
                    <option value="Norueguês">Norueguês</option>
                    <option value="Polonês">Polonês</option>
                    <option value="Português">Português</option>
                    <option value="Romeno">Romeno</option>
                    <option value="Russo">Russo</option>
                    <option value="Sueco">Sueco</option>
                    <option value="Tailandês">Tailandês</option>
                    <option value="Tcheco">Tcheco</option>
                    <option value="Turco">Turco</option>
                    <option value="Ucraniano">Ucraniano</option>
                  </select>
                </label>

                <label className="field-label"> Nível
                  <select value={item.nivel} onChange={(e) => handleArrayChange(i, "idiomas", "nivel", e.target.value)}>
                    <option value="">Nível</option>
                    <option value="Básico">Básico</option>
                    <option value="Intermediário">Intermediário</option>
                    <option value="Avançado">Avançado</option>
                    <option value="Fluente">Fluente</option>
                    <option value="Nativo">Nativo</option>
                  </select>
                </label>

                <button type="button" className="remove-btn" onClick={() => removeItem("idiomas", i)}>Remover idioma</button>

                <hr className="divider" />
              </div>
            ))}

            <button type="button" className="add-btn" onClick={() => addItem("idiomas", { idioma: "", nivel: "" })}>+ Adicionar idioma</button>
          </div>

          {/* CNH dinamica: aparece apenas se usuario clicar em "+ Adicionar CNH" */}
          <div className="dynamic-field">
            <div className="section-title">CNH</div>
            {!showCNHField ? (
              <button type="button" className="add-btn" onClick={() => setShowCNHField(true)}>+ Adicionar CNH</button>
            ) : (
              <div className="multi-input stack">
                <label className="field-label">Categoria CNH
                  <input name="cnh" value={form.cnh} onChange={handleChange} placeholder="Informe sua categoria (ex: B)" />
                </label>
                <button type="button" className="remove-btn" onClick={() => { setForm((p) => ({ ...p, cnh: "" })); setShowCNHField(false); }}>Remover CNH</button>
              </div>
            )}
          </div>

          <div className="form-actions">
            <button type="submit" disabled={loading} className="submit-btn">{loading ? "Gerando..." : "Gerar Currículo"}</button>

            {/* Botão de preview com caixa e ícone olho */}
            <div className="preview-box" onClick={togglePreview} role="button" tabIndex={0}>
              <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8S1 12 1 12z"></path>
                <circle cx="12" cy="12" r="3"></circle>
              </svg>
              <span>PREVIEW DO CURRÍCULO</span>
            </div>
          </div>
        </form>
      </section>

      {/* PREVIEW (invisível por padrão) */}
      <section className={`preview-section ${previewOpen ? "open" : "closed"}`} aria-hidden={!previewOpen}>
        <div id="preview-curriculo" ref={previewRef} className="curriculo-template">
          {/* Cabeçalho: nome centralizado */}
          <header className="cv-header">
            <h2 className="cv-name">{form.nome || "SEU NOME AQUI"}</h2>
            <div className="cv-contacts">
              {/* email | telefone | cidade | idade */}
              {form.email && <span>{form.email}</span>}
              {form.email && (form.telefone || form.cidade || form.data_nascimento) && <span className="sep">|</span>}
              {form.telefone && <span>{form.telefone}</span>}
              {form.telefone && (form.cidade || form.data_nascimento) && <span className="sep">|</span>}
              {form.cidade && <span>{form.cidade}</span>}
              {form.data_nascimento && <span className="sep">|</span>}
              {form.data_nascimento && <span>{calcAge(form.data_nascimento)} anos</span>}
            </div>
          </header>

          {/* Objetivo */}
          <section className="cv-section">
            <div className="cv-section-title">Objetivo</div>
            <div className="cv-section-text">{form.objetivo}</div>
          </section>

          {/* Formações */}
          <section className="cv-section">
            <div className="cv-section-title">Formação Acadêmica</div>
            <div className="cv-section-text">
              {form.formacoes.length === 0 && <div>-</div>}
              {form.formacoes.map((f, i) => (
                <div key={i} className="cv-item">
                  <div className="cv-item-title">{f.curso} — {f.escola}</div>
                  <div className="cv-item-sub">{f.inicio}{f.fim ? ` — ${f.fim}` : (f.status === "Cursando" ? " — Cursando" : "")}</div>
                </div>
              ))}
            </div>
          </section>

          {/* Experiências */}
          <section className="cv-section">
            <div className="cv-section-title">Experiência Profissional</div>
            <div className="cv-section-text">
              {form.experiencias.length === 0 && <div>-</div>}
              {form.experiencias.map((exp, i) => (
                <div key={i} className="cv-item">
                  <div className="cv-item-title">{exp.cargo} — {exp.empresa}</div>
                  <div className="cv-item-sub">{exp.inicio}{exp.fim ? ` — ${exp.fim}` : (exp.trabalhoAtual ? " — Atualmente" : "")}</div>
                  {(exp.atribuicoes || []).length > 0 && <ul className="cv-list">
                    {(exp.atribuicoes || []).map((a, j) => <li key={j}>{a}</li>)}
                  </ul>}
                </div>
              ))}
            </div>
          </section>

          {/* Cursos */}
          <section className="cv-section">
            <div className="cv-section-title">Cursos</div>
            <div className="cv-section-text">
              {form.cursos.length === 0 && <div>-</div>}
              {form.cursos.map((c, i) => (
                <div key={i} className="cv-item">
                  <div className="cv-item-title">{c.curso} — {c.instituicao}</div>
                  <div className="cv-item-sub">{c.inicio}{c.fim ? ` — ${c.fim}` : (c.status === "Cursando" ? " — Cursando" : "")}</div>
                </div>
              ))}
            </div>
          </section>

          {/* Conhecimentos */}
          <section className="cv-section">
            <div className="cv-section-title">Conhecimentos</div>
            <div className="cv-section-text">
              {form.conhecimentos.length === 0 && <div>-</div>}
              {form.conhecimentos.map((k, i) => <div key={i} className="cv-item">• {k.descricao}</div>)}
            </div>
          </section>

          {/* Idiomas */}
          <section className="cv-section">
            <div className="cv-section-title">Idiomas</div>
            <div className="cv-section-text">
              {form.idiomas.length === 0 && <div>-</div>}
              {form.idiomas.map((l, i) => <div key={i} className="cv-item">{l.idioma} — {l.nivel}</div>)}
            </div>
          </section>

          {/* CNH (apenas se preenchido) */}
          {form.cnh && (
            <section className="cv-section">
              <div className="cv-section-title">CNH</div>
              <div className="cv-section-text">{form.cnh}</div>
            </section>
          )}

          <footer className="cv-footer">
            Gerado por Gerador de Currículos
          </footer>
        </div>
      </section>
    </div>
  );
}

export default Gerador;
