import React, { useState } from "react";
import "./App.css";

function App() {
  const emptyFormation = { curso: "", escola: "", status: "" };
  const emptyConhecimento = { topico: "", descricao: "" };
  const emptyQual = { curso: "", escola: "", status: "" };
  const emptyExperiencia = { empresa: "", cargo: "", acoes: [""] };
  const emptyIdioma = { idioma: "", nivel: "Básico (fala)" };

  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    cidade: "",
    data_nascimento: "",
    objetivo: "",
    formacoes: [emptyFormation],
    conhecimentos: [emptyConhecimento],
    cursosQual: [emptyQual],
    experiencias: [emptyExperiencia],
    cnh: "",
    idiomas: [emptyIdioma],
  });

  const [loading, setLoading] = useState(false);

  // genéricos
  const setField = (name, value) => {
    setForm((p) => ({ ...p, [name]: value }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setField(name, value);
  };

  // arrays de objetos
  const updateArrayItem = (field, index, key, value) => {
    const arr = [...form[field]];
    arr[index] = { ...arr[index], [key]: value };
    setField(field, arr);
  };

  const addArrayItem = (field, emptyItem) => {
    setField(field, [...form[field], emptyItem]);
  };

  const removeArrayItem = (field, index) => {
    const arr = [...form[field]];
    if (arr.length === 1) return; // manter ao menos 1
    arr.splice(index, 1);
    setField(field, arr);
  };

  // experiencia -> adicionar atribuição (string)
  const updateExperienciaAtrib = (expIndex, atribIndex, value) => {
    const exps = [...form.experiencias];
    exps[expIndex].acoes[atribIndex] = value;
    setField("experiencias", exps);
  };
  const addExperienciaAtrib = (expIndex) => {
    const exps = [...form.experiencias];
    exps[expIndex].acoes.push("");
    setField("experiencias", exps);
  };
  const removeExperienciaAtrib = (expIndex, atribIndex) => {
    const exps = [...form.experiencias];
    if (exps[expIndex].acoes.length === 1) return;
    exps[expIndex].acoes.splice(atribIndex, 1);
    setField("experiencias", exps);
  };

  // idiomas níveis
  const niveis = [
    "Básico (fala)",
    "Básico (escrito)",
    "Básico (escuta)",
    "Intermediário",
    "Avançado",
    "Fluente",
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // enviamos as estruturas como JSON (backend processa)
    const dataToSend = { ...form };

    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/generate_pdf`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dataToSend),
      });

      if (!res.ok) {
        const txt = await res.text().catch(() => "");
        throw new Error("Erro ao gerar PDF. " + (txt || ""));
      }

      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement("a");
      const safeName = (form.nome || "curriculo").replace(/\s+/g, "_");
      link.href = url;
      link.download = `curriculo_${safeName}.pdf`;
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (err) {
      alert(err.message || "Erro desconhecido");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <section className="form-section">
        <h1>GERADOR DE CURRÍCULO ONLINE</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-grid">
            <label>
              Nome
              <input name="nome" value={form.nome} onChange={handleChange} required />
            </label>
            <label>
              Telefone
              <input name="telefone" value={form.telefone} onChange={handleChange} />
            </label>
            <label>
              Email
              <input name="email" value={form.email} onChange={handleChange} type="email" />
            </label>
            <label>
              Cidade
              <input name="cidade" value={form.cidade} onChange={handleChange} />
            </label>
          </div>

          <div className="input-grid">
            <label>
              Data de nascimento
              <input name="data_nascimento" value={form.data_nascimento} onChange={handleChange} type="date" />
            </label>
            <label>
              CNH
              <input name="cnh" value={form.cnh} onChange={handleChange} />
            </label>
            <label>
              Idioma principal
              <input
                name="idioma_principal"
                value={form.idioma_principal || ""}
                onChange={(e) => setField("idioma_principal", e.target.value)}
              />
            </label>
            <label>
              {/* placeholder para manter grid */}
              &nbsp;
              <input style={{ visibility: "hidden" }} />
            </label>
          </div>

          <label>
            Objetivo Profissional
            <textarea name="objetivo" value={form.objetivo} onChange={handleChange} rows={3} />
          </label>

          {/* Formação Acadêmica */}
          <div className="dynamic-field">
            <label>Formação Acadêmica</label>
            {form.formacoes.map((f, i) => (
              <div className="card" key={i}>
                <input
                  placeholder="Nome do curso"
                  value={f.curso}
                  onChange={(e) => updateArrayItem("formacoes", i, "curso", e.target.value)}
                />
                <input
                  placeholder="Nome da escola"
                  value={f.escola}
                  onChange={(e) => updateArrayItem("formacoes", i, "escola", e.target.value)}
                />
                <input
                  placeholder="Status (ex: Concluído / Cursando)"
                  value={f.status}
                  onChange={(e) => updateArrayItem("formacoes", i, "status", e.target.value)}
                />
                <div className="row-controls">
                  <button type="button" className="add-btn" onClick={() => addArrayItem("formacoes", emptyFormation)}>
                    + Adicionar formação
                  </button>
                  <button type="button" className="add-btn" onClick={() => removeArrayItem("formacoes", i)}>
                    - Remover
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Conhecimentos */}
          <div className="dynamic-field">
            <label>Conhecimentos</label>
            {form.conhecimentos.map((c, i) => (
              <div className="card" key={i}>
                <input
                  placeholder="Tópico (Ex: Frontend)"
                  value={c.topico}
                  onChange={(e) => updateArrayItem("conhecimentos", i, "topico", e.target.value)}
                />
                <textarea
                  placeholder="Descrição / exemplos"
                  value={c.descricao}
                  onChange={(e) => updateArrayItem("conhecimentos", i, "descricao", e.target.value)}
                />
                <div className="row-controls">
                  <button type="button" className="add-btn" onClick={() => addArrayItem("conhecimentos", emptyConhecimento)}>
                    + Adicionar conhecimento
                  </button>
                  <button type="button" className="add-btn" onClick={() => removeArrayItem("conhecimentos", i)}>
                    - Remover
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Cursos de Qualificação */}
          <div className="dynamic-field">
            <label>Cursos de Qualificação</label>
            {form.cursosQual.map((c, i) => (
              <div className="card" key={i}>
                <input
                  placeholder="Nome do curso"
                  value={c.curso}
                  onChange={(e) => updateArrayItem("cursosQual", i, "curso", e.target.value)}
                />
                <input
                  placeholder="Nome da escola"
                  value={c.escola}
                  onChange={(e) => updateArrayItem("cursosQual", i, "escola", e.target.value)}
                />
                <input
                  placeholder="Status"
                  value={c.status}
                  onChange={(e) => updateArrayItem("cursosQual", i, "status", e.target.value)}
                />
                <div className="row-controls">
                  <button type="button" className="add-btn" onClick={() => addArrayItem("cursosQual", emptyQual)}>
                    + Adicionar curso
                  </button>
                  <button type="button" className="add-btn" onClick={() => removeArrayItem("cursosQual", i)}>
                    - Remover
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Experiências */}
          <div className="dynamic-field">
            <label>Experiência Profissional</label>
            {form.experiencias.map((exp, i) => (
              <div className="card" key={i}>
                <input
                  placeholder="Nome da empresa"
                  value={exp.empresa}
                  onChange={(e) => updateArrayItem("experiencias", i, "empresa", e.target.value)}
                />
                <input
                  placeholder="Cargo"
                  value={exp.cargo}
                  onChange={(e) => updateArrayItem("experiencias", i, "cargo", e.target.value)}
                />
                <label>Atribuições</label>
                {exp.acoes.map((a, ai) => (
                  <div key={ai} style={{ display: "flex", gap: "0.5rem", marginTop: "0.4rem" }}>
                    <input
                      placeholder={`Atribuição ${ai + 1}`}
                      value={a}
                      onChange={(e) => updateExperienciaAtrib(i, ai, e.target.value)}
                      style={{ flex: 1 }}
                    />
                    <button type="button" className="add-btn" onClick={() => removeExperienciaAtrib(i, ai)}>
                      -
                    </button>
                  </div>
                ))}
                <div className="row-controls">
                  <button type="button" className="add-btn" onClick={() => addExperienciaAtrib(i)}>
                    + Adicionar atribuição
                  </button>
                  <button type="button" className="add-btn" onClick={() => addArrayItem("experiencias", emptyExperiencia)}>
                    + Nova experiência
                  </button>
                  <button type="button" className="add-btn" onClick={() => removeArrayItem("experiencias", i)}>
                    - Remover experiência
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Idiomas */}
          <div className="dynamic-field">
            <label>Idiomas</label>
            {form.idiomas.map((idi, i) => (
              <div className="card" key={i}>
                <input
                  placeholder="Idioma"
                  value={idi.idioma}
                  onChange={(e) => updateArrayItem("idiomas", i, "idioma", e.target.value)}
                />
                <select
                  value={idi.nivel}
                  onChange={(e) => updateArrayItem("idiomas", i, "nivel", e.target.value)}
                >
                  {niveis.map((n) => (
                    <option key={n} value={n}>
                      {n}
                    </option>
                  ))}
                </select>
                <div className="row-controls">
                  <button type="button" className="add-btn" onClick={() => addArrayItem("idiomas", emptyIdioma)}>
                    + Adicionar idioma
                  </button>
                  <button type="button" className="add-btn" onClick={() => removeArrayItem("idiomas", i)}>
                    - Remover
                  </button>
                </div>
              </div>
            ))}
          </div>

          <button type="submit" disabled={loading} className="submit-btn">
            {loading ? "Gerando..." : "Gerar PDF"}
          </button>
        </form>
      </section>
    </div>
  );
}

export default App;
