// Gerador.jsx
import React, { useState } from "react";
import "./gerador.css"; // seu css de form
import "./pdf.css"; // O CSS do modelo de currículo (colocar na mesma pasta)
import { gerarPDF } from "./pdf"; // arquivo pdf.js (mesma pasta)
import "../../App.css";
import Login from "../../components/login/login";

function Gerador() {
  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    data_nascimento: "",
    cidade: "",
    objetivo: "",
    formacoes: [],
    conhecimentos: [],
    cursos: [],
    experiencias: [],
    idiomas: [],
    cnh: "",
  });
  const [loading, setLoading] = useState(false);

  const saferCapitalize = (text) => {
    if (!text) return "";
    return text
      .split(" ")
      .map((w) => {
        if (!w) return "";
        const first = w[0].toUpperCase();
        const rest = w.slice(1).toLowerCase();
        return first + rest;
      })
      .join(" ");
  };

  const formatPhone = (onlyDigits) => {
    const d = onlyDigits.replace(/\D/g, "");
    let out = d;
    if (d.length <= 2) {
      out = "(" + d;
    } else if (d.length <= 6) {
      out = `(${d.slice(0, 2)}) ${d.slice(2)}`;
    } else if (d.length <= 10) {
      out = `(${d.slice(0, 2)}) ${d.slice(2, d.length - 4)}-${d.slice(d.length - 4)}`;
    } else {
      out = `(${d.slice(0, 2)}) ${d.slice(2, 7)}-${d.slice(7, 11)}`;
    }
    return out;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let val = value;

    if (name === "telefone") {
      const digits = val.replace(/\D/g, "").slice(0, 11);
      val = formatPhone(digits);
    }

    if (name === "email") {
      val = val.toLowerCase();
    }

    if (name === "cnh") {
      val = val.replace(/[^0-9A-Za-z]/g, "").toUpperCase();
    }

    if (name === "nome" || name === "cidade") {
      val = saferCapitalize(val);
    }

    setForm((prev) => ({ ...prev, [name]: val }));
  };

  const handleArrayChange = (index, field, subField, value) => {
    const updated = [...form[field]];
    updated[index] = { ...updated[index], [subField]: value };
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  const addItem = (field, itemTemplate) => {
    setForm((prev) => ({ ...prev, [field]: [...prev[field], itemTemplate] }));
  };

  const removeItem = (field, index) => {
    const updated = [...form[field]];
    updated.splice(index, 1);
    setForm((prev) => ({ ...prev, [field]: updated }));
  };

  const calcAgeYears = (dateStr) => {
    if (!dateStr) return "";
    const dob = new Date(dateStr);
    const today = new Date();
    let age = today.getFullYear() - dob.getFullYear();
    const m = today.getMonth() - dob.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < dob.getDate())) {
      age--;
    }
    return age;
  };

  // remover preview antigo (o usuário disse que não quer Flask) - deixei só frontend
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.nome || !form.email || !form.telefone || !form.objetivo) {
      alert("Preencha os campos obrigatórios!");
      return;
    }
    setLoading(true);
    try {
      // a função gerarPDF usa o elemento com id preview-curriculo presente no DOM (renderizado abaixo)
      await gerarPDF();
    } catch (err) {
      console.error(err);
      alert("Erro ao gerar PDF");
    } finally {
      setLoading(false);
    }
  };

  const [menuOpen, setMenuOpen] = useState(false);
  const toggleMenu = () => setMenuOpen(!menuOpen);

  // render de listas (para o preview) — devolve linhas simples caso existam
  const renderFormacoes = () =>
    form.formacoes.length === 0 ? null : (
      <div className="pdf-section">
        <div className="pdf-section-title">Formação Acadêmica</div>
        <ul className="pdf-list">
          {form.formacoes.map((f, i) => (
            <li key={i}>
              <div className="pdf-item-title">
                {f.curso || ""}
                {f.escola ? ` — ${f.escola}` : ""}
              </div>
              <div className="pdf-item-sub">
                {f.inicio ? f.inicio : ""}
                {f.fim ? ` — ${f.fim}` : f.status === "Cursando" ? " — Cursando" : ""}
              </div>
            </li>
          ))}
        </ul>
      </div>
    );

  const renderExperiencias = () =>
    form.experiencias.length === 0 ? null : (
      <div className="pdf-section">
        <div className="pdf-section-title">Experiência Profissional</div>
        <ul className="pdf-list">
          {form.experiencias.map((exp, i) => (
            <li key={i}>
              <div className="pdf-item-title">
                {exp.cargo ? `${exp.cargo}` : ""} {exp.empresa ? ` — ${exp.empresa}` : ""}
              </div>
              <div className="pdf-item-sub">
                {exp.inicio ? exp.inicio : ""} {exp.fim ? ` — ${exp.fim}` : exp.trabalhoAtual ? " — Atual" : ""}
              </div>
              {(exp.atribuicoes || []).length > 0 && (
                <ul className="pdf-sublist">
                  {(exp.atribuicoes || []).map((a, j) => <li key={j}>{a}</li>)}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>
    );

  const renderCursos = () =>
    form.cursos.length === 0 ? null : (
      <div className="pdf-section">
        <div className="pdf-section-title">Cursos</div>
        <ul className="pdf-list">
          {form.cursos.map((c, i) => (
            <li key={i}>
              <div className="pdf-item-title">{c.curso}{c.instituicao ? ` — ${c.instituicao}` : ""}</div>
              <div className="pdf-item-sub">{c.inicio ? c.inicio : ""}{c.fim ? ` — ${c.fim}` : c.status === "Cursando" ? " — Cursando" : ""}</div>
            </li>
          ))}
        </ul>
      </div>
    );

  const renderConhecimentos = () =>
    form.conhecimentos.length === 0 ? null : (
      <div className="pdf-section">
        <div className="pdf-section-title">Conhecimentos</div>
        <ul className="pdf-list inline-list">
          {form.conhecimentos.map((k, i) => <li key={i}>{k.descricao}</li>)}
        </ul>
      </div>
    );

  const renderIdiomas = () =>
    form.idiomas.length === 0 ? null : (
      <div className="pdf-section">
        <div className="pdf-section-title">Idiomas</div>
        <ul className="pdf-list inline-list">
          {form.idiomas.map((it, i) => <li key={i}>{it.idioma} ({it.nivel})</li>)}
        </ul>
      </div>
    );

  return (
    <div className="app-container gerador-container">
      <nav className="top-navbar">
        <div className="left-section">
          <a href="#top">
            <img src="/logo.png" alt="Logo" className="logo" />
          </a>
        </div>
        <div className={`right-section ${menuOpen ? "open" : ""}`}>
          <a href="/" onClick={() => setMenuOpen(false)}>Voltar para a página inicial</a>
          <Login />
        </div>

        <div className="menu-toggle" onClick={toggleMenu}>
          <i className={`bi ${menuOpen ? "bi-x" : "bi-list"}`}></i>
        </div>
      </nav>

      <section className="form-section" id="form-section">
        <h1>GERADOR DE CURRÍCULO ONLINE</h1>
        <form onSubmit={handleSubmit}>
          <div className="input-grid">
            <label className="field-label">
              Nome
              <input name="nome" value={form.nome} onChange={handleChange} required placeholder="Digite seu nome completo" />
            </label>

            <label className="field-label">
              Telefone
              <input name="telefone" value={form.telefone} onChange={handleChange} placeholder="Apenas números" required />
            </label>

            <label className="field-label">
              Email
              <input name="email" value={form.email} onChange={handleChange} type="email" required placeholder="nome@exemplo.com" />
            </label>

            <label className="field-label">
              Cidade
              <input name="cidade" value={form.cidade} onChange={handleChange} placeholder="Informe sua cidade" />
            </label>

            <label className="field-label">
              Data de nascimento
              <input name="data_nascimento" value={form.data_nascimento} onChange={handleChange} type="date" />
            </label>

            <label className="field-label">
              CNH
              <input name="cnh" value={form.cnh} onChange={handleChange} placeholder="Informe sua categoria" />
            </label>
          </div>

          <label className="field-label">
            Objetivo Profissional
            <textarea name="objetivo" value={form.objetivo} onChange={handleChange} rows={4} required placeholder="Digite aqui o seus objetivos e metas" />
          </label>

          {/* Dinâmicos (formacoes, conhecimentos, cursos, experiencias, idiomas) */}
          <div className="dynamic-field">
            <div className="section-title">Formação Acadêmica</div>
            {form.formacoes.map((item, i) => {
              const status = item.status || "";
              return (
                <div key={i} className="multi-input">
                  <input placeholder="Curso" value={item.curso} onChange={(e) => handleArrayChange(i, "formacoes", "curso", saferCapitalize(e.target.value))} />
                  <input placeholder="Instituição" value={item.escola} onChange={(e) => handleArrayChange(i, "formacoes", "escola", saferCapitalize(e.target.value))} />
                  <select value={status} onChange={(e) => handleArrayChange(i, "formacoes", "status", e.target.value)}>
                    <option value="">Status</option>
                    <option value="Concluído">Concluído</option>
                    <option value="Cursando">Cursando</option>
                    <option value="Trancado">Trancado</option>
                  </select>
                  <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "formacoes", "inicio", e.target.value)} />
                  <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "formacoes", "fim", e.target.value)} />
                  <button type="button" onClick={() => removeItem("formacoes", i)}>Remover</button>
                </div>
              );
            })}
            <button type="button" onClick={() => addItem("formacoes", { curso: "", escola: "", status: "", inicio: "", fim: "" })}>+ Adicionar formação</button>
          </div>

          <div className="dynamic-field">
            <div className="section-title">Conhecimentos</div>
            {form.conhecimentos.map((item, i) => (
              <div key={i} className="multi-input">
                <input placeholder="Conhecimento" value={item.descricao} onChange={(e) => handleArrayChange(i, "conhecimentos", "descricao", e.target.value)} />
                <button type="button" onClick={() => removeItem("conhecimentos", i)}>Remover</button>
              </div>
            ))}
            <button type="button" onClick={() => addItem("conhecimentos", { descricao: "" })}>+ Adicionar conhecimento</button>
          </div>

          <div className="dynamic-field">
            <div className="section-title">Cursos</div>
            {form.cursos.map((item, i) => (
              <div key={i} className="multi-input">
                <input placeholder="Curso" value={item.curso} onChange={(e) => handleArrayChange(i, "cursos", "curso", saferCapitalize(e.target.value))} />
                <input placeholder="Instituição" value={item.instituicao} onChange={(e) => handleArrayChange(i, "cursos", "instituicao", saferCapitalize(e.target.value))} />
                <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "cursos", "inicio", e.target.value)} />
                <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "cursos", "fim", e.target.value)} />
                <button type="button" onClick={() => removeItem("cursos", i)}>Remover</button>
              </div>
            ))}
            <button type="button" onClick={() => addItem("cursos", { curso: "", instituicao: "", inicio: "", fim: "", status: "" })}>+ Adicionar curso</button>
          </div>

          <div className="dynamic-field">
            <div className="section-title">Experiências</div>
            {form.experiencias.map((item, i) => (
              <div key={i} className="multi-input">
                <input placeholder="Empresa" value={item.empresa} onChange={(e) => handleArrayChange(i, "experiencias", "empresa", saferCapitalize(e.target.value))} />
                <input placeholder="Cargo" value={item.cargo} onChange={(e) => handleArrayChange(i, "experiencias", "cargo", saferCapitalize(e.target.value))} />
                <input type="month" value={item.inicio} onChange={(e) => handleArrayChange(i, "experiencias", "inicio", e.target.value)} />
                <input type="month" value={item.fim} onChange={(e) => handleArrayChange(i, "experiencias", "fim", e.target.value)} />
                <button type="button" onClick={() => removeItem("experiencias", i)}>Remover</button>

                <div>
                  <label>
                    <input type="checkbox" checked={!!item.trabalhoAtual} onChange={(e) => handleArrayChange(i, "experiencias", "trabalhoAtual", e.target.checked)} />
                    Trabalho atual
                  </label>
                </div>

                <div>
                  {(item.atribuicoes || []).map((atr, j) => (
                    <div key={j} style={{ display: "flex", gap: 8, alignItems: "center" }}>
                      <input placeholder={`Atribuição ${j + 1}`} value={atr} onChange={(e) => {
                        const exp = [...form.experiencias];
                        exp[i].atribuicoes[j] = e.target.value;
                        setForm((prev) => ({ ...prev, experiencias: exp }));
                      }} />
                      <button type="button" onClick={() => {
                        const exp = [...form.experiencias];
                        exp[i].atribuicoes.splice(j, 1);
                        setForm((prev) => ({ ...prev, experiencias: exp }));
                      }}>✕</button>
                    </div>
                  ))}
                  <button type="button" onClick={() => {
                    const exp = [...form.experiencias];
                    exp[i].atribuicoes = [...(exp[i].atribuicoes || []), ""];
                    setForm((prev) => ({ ...prev, experiencias: exp }));
                  }}>+ Atribuição</button>
                </div>
              </div>
            ))}
            <button type="button" onClick={() => addItem("experiencias", { empresa: "", cargo: "", trabalhoAtual: false, inicio: "", fim: "", atribuicoes: [] })}>+ Adicionar experiência</button>
          </div>

          <div className="dynamic-field">
            <div className="section-title">Idiomas</div>
            {form.idiomas.map((item, i) => (
              <div key={i} className="multi-input">
                <select value={item.idioma} onChange={(e) => handleArrayChange(i, "idiomas", "idioma", e.target.value)}>
                  <option value="">Idioma</option>
                  <option>Português</option>
                  <option>Inglês</option>
                  <option>Espanhol</option>
                  <option>Francês</option>
                  <option>Alemão</option>
                  <option>Outros</option>
                </select>
                <select value={item.nivel} onChange={(e) => handleArrayChange(i, "idiomas", "nivel", e.target.value)}>
                  <option value="">Nível</option>
                  <option>Básico</option>
                  <option>Intermediário</option>
                  <option>Avançado</option>
                  <option>Fluente</option>
                </select>
                <button type="button" onClick={() => removeItem("idiomas", i)}>Remover</button>
              </div>
            ))}
            <button type="button" onClick={() => addItem("idiomas", { idioma: "", nivel: "" })}>+ Adicionar idioma</button>
          </div>

          <button type="submit" disabled={loading} className="submit-btn">
            {loading ? "Gerando..." : "Gerar Currículo (PDF)"}
          </button>
        </form>
      </section>

      {/* PREVIEW DO CURRÍCULO — este elemento será usado para gerar o PDF */}
      <section className="preview-section">
        <div id="preview-curriculo" className="pdf-page">
          <div className="pdf-header">
            <div className="pdf-name">{form.nome || "Nome Completo"}</div>
            <div className="pdf-contacts">
              {/* email | telefone | cidade | idade */}
              {form.email ? form.email : ""}
              {form.email && (form.telefone || form.cidade || form.data_nascimento) ? " | " : ""}
              {form.telefone ? form.telefone : ""}
              {form.telefone && (form.cidade || form.data_nascimento) ? " | " : ""}
              {form.cidade ? form.cidade : ""}
              {form.cidade && form.data_nascimento ? " | " : ""}
              {form.data_nascimento ? `${calcAgeYears(form.data_nascimento)} anos` : ""}
            </div>
          </div>

          <div className="pdf-section">
            <div className="pdf-section-title">Objetivo</div>
            <div className="pdf-text">{form.objetivo}</div>
          </div>

          {renderFormacoes()}
          {renderExperiencias()}
          {renderCursos()}
          {renderConhecimentos()}
          {renderIdiomas()}

          <div className="pdf-footer">
            <div>CNH: {form.cnh || "-"}</div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Gerador;
