import React, { useState } from "react";
import html2pdf from "html2pdf.js";
import "./pdf.css";

function Gerador() {
  // -----------------------------
  // ESTADO DO FORMULÁRIO
  // -----------------------------
  const [form, setForm] = useState({
    nome: "",
    telefone: "",
    email: "",
    cidade: "",
    objetivo: "",
    formacoes: [{ curso: "", instituicao: "", inicio: "", fim: "" }],
    experiencias: [{
      empresa: "",
      cargo: "",
      inicio: "",
      fim: "",
      atividades: ""
    }],
    cursos: [{ nome: "", instituicao: "", carga: "" }],
    idiomas: [{ idioma: "", nivel: "" }]
  });

  // -----------------------------
  // FUNÇÃO PARA ALTERAR CAMPOS
  // -----------------------------
  const handleChange = (e) => {
    const { name, value } = e.target;

    // TELEFONE (apenas números)
    if (name === "telefone") {
      const limpo = value.replace(/\D/g, "");
      setForm({ ...form, telefone: limpo });
      return;
    }

    // EMAIL normal
    setForm({ ...form, [name]: value });
  };

  // -----------------------------
  // FUNÇÕES PARA ARRAYS (FORMACAO / EXPERIENCIAS / CURSOS / IDIOMAS)
  // -----------------------------
  const handleArrayChange = (e, index, field, arrayName) => {
    const novo = [...form[arrayName]];
    novo[index][field] = e.target.value;
    setForm({ ...form, [arrayName]: novo });
  };

  const addItem = (arrayName, itemBase) => {
    setForm({ ...form, [arrayName]: [...form[arrayName], itemBase] });
  };

  // -----------------------------
  // VALIDAÇÕES
  // -----------------------------
  const camposObrigatorios = [
    "nome",
    "telefone",
    "email",
    "cidade",
    "objetivo"
  ];

  const validar = () => {
    for (let campo of camposObrigatorios) {
      if (!form[campo] || form[campo].trim() === "") {
        alert(`Preencha o campo obrigatório: ${campo.toUpperCase()}`);
        return false;
      }
    }

    // email
    const regexEmail = /\S+@\S+\.\S+/;
    if (!regexEmail.test(form.email)) {
      alert("Informe um email válido.");
      return false;
    }

    // telefone mínimo
    if (form.telefone.length < 8) {
      alert("Telefone inválido.");
      return false;
    }

    // objetivo com mínimo
    if (form.objetivo.length < 10) {
      alert("O objetivo profissional deve ter pelo menos 10 caracteres.");
      return false;
    }

    return true;
  };

  // -----------------------------
  // GERAR PDF
  // -----------------------------
  const gerarPDF = () => {
    if (!validar()) return;

    const areaPDF = document.getElementById("area-pdf");

    const opt = {
      margin: 5,
      filename: `curriculo_${form.nome.replace(/\s+/g, "_")}.pdf`,
      image: { type: "jpeg", quality: 1 },
      html2canvas: { scale: 2 },
      jsPDF: { unit: "mm", format: "a4", orientation: "portrait" }
    };

    html2pdf().set(opt).from(areaPDF).save();
  };

  // ------------------------------------------
  // TEMPLATE INVISÍVEL DO PDF
  // ------------------------------------------
  const TemplatePDF = () => (
    <div id="area-pdf" className="pdf-container">
      <h1 className="nome">{form.nome}</h1>

      <p className="contato">
        <strong>Telefone:</strong> {form.telefone}<br />
        <strong>Email:</strong> {form.email}<br />
        <strong>Cidade:</strong> {form.cidade}
      </p>

      <hr />

      <h2>Objetivo Profissional</h2>
      <p>{form.objetivo}</p>

      <hr />

      <h2>Formação</h2>
      {form.formacoes.map((f, i) => (
        <p key={i}>
          <strong>{f.curso}</strong><br />
          {f.instituicao} — {f.inicio} até {f.fim}
        </p>
      ))}

      <hr />

      <h2>Experiências</h2>
      {form.experiencias.map((exp, i) => (
        <p key={i}>
          <strong>{exp.cargo}</strong> — {exp.empresa}<br />
          {exp.inicio} até {exp.fim}<br />
          <em>{exp.atividades}</em>
        </p>
      ))}

      <hr />

      <h2>Cursos</h2>
      {form.cursos.map((c, i) => (
        <p key={i}>
          <strong>{c.nome}</strong> — {c.instituicao}<br />
          {c.carga} horas
        </p>
      ))}

      <hr />

      <h2>Idiomas</h2>
      {form.idiomas.map((id, i) => (
        <p key={i}>
          <strong>{id.idioma}</strong>: {id.nivel}
        </p>
      ))}

      <br />
      <p style={{ fontSize: "11px", textAlign: "right", opacity: 0.7 }}>
        Curriculum gerado automaticamente — {new Date().toLocaleDateString()}
      </p>
    </div>
  );

  // ------------------------------------------
  // RENDER DO FORMULÁRIO
  // ------------------------------------------
  return (
    <div className="pagina-gerador">

      {/* Template invisível */}
      <TemplatePDF />

      <h1>Gerador de Currículo</h1>

      <div className="formulario">

        {/* NOME */}
        <label>Nome <span className="obg">*</span></label>
        <input name="nome" value={form.nome} onChange={handleChange} />

        {/* TELEFONE */}
        <label>Telefone <span className="obg">*</span></label>
        <input name="telefone" value={form.telefone} onChange={handleChange} />

        {/* EMAIL */}
        <label>Email <span className="obg">*</span></label>
        <input name="email" value={form.email} onChange={handleChange} />

        {/* CIDADE */}
        <label>Cidade <span className="obg">*</span></label>
        <input name="cidade" value={form.cidade} onChange={handleChange} />

        {/* OBJETIVO */}
        <label>Objetivo Profissional <span className="obg">*</span></label>
        <textarea name="objetivo" value={form.objetivo} onChange={handleChange} />

        <button className="btn-gerar" onClick={gerarPDF}>
          Gerar PDF
        </button>
      </div>
    </div>
  );
}

export default Gerador;
